mapper.select:
SELECT COUNT(*) AS TOTAL FROM FORUMS
WHERE TYPE = #{type}


        AND SUBJECT LIKE #{likeKeyword}
        AND SUBJECT LIKE #{likeKeyword} OR CONTENT LIKE #{likeKeyword}
        AND USER_NAME = #{keyword}

SELECT f.ID, f.USER_NAME, f.SUBJECT, f.CONTENT, f.REG_DATE, f.HITS, f.TYPE, c.COMMENT_CNT FROM (SELECT ROW_NUMBER() OVER (ORDER BY ID DESC) AS SEQ, ID, USER_NAME, SUBJECT, CONTENT, REG_DATE, HITS, TYPE FROM FORUMS WHERE TYPE = #{type}
AND SUBJECT LIKE #{likeKeyword}
AND SUBJECT LIKE #{likeKeyword} OR TYPE = #{type} AND CONTENT LIKE #{likeKeyword}
AND USER_NAME = #{keyword}
) f, (SELECT FORUM_ID, COUNT(*) COMMENT_CNT FROM COMMENTS GROUP BY FORUM_ID) c WHERE f.SEQ BETWEEN #{start} AND #{end} AND f.ID = c.FORUM_ID(+) ORDER BY ID DESC
